import React, { Component } from "react";

class CharacterList extends Component {
  handleOnScroll = () => {
    if (this.props.hasnext) {
      this.props.onLoadMore();
    }
  };
  render() {
    if (!this.props.characters && this.props.loading) return <p>Loading....</p>;
    const characters = this.props.characters.edges || [];
    return (
    <>
        {
          characters.map(({character}, idx) => {
            return (
            <div className="link" key={idx} to={`/${character.id}`}>
              <div>
                <h2>
                  {character.name}
                </h2>
                <p>{character.species === null ? "Human" : character.species.name } from {character.homeworld.name}</p>
              </div>
            </div>

            )},onLoadMore())
        }
    </>
    )
  }
}

export default CharacterList;
